// src/App.js

import React, { useState } from 'react';
import './App.css'; // Keep your custom styles
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import RuleForm from './components/RuleForm';
import RuleEvaluator from './components/RuleEvaluator';

function App() {
  const [ruleCreated, setRuleCreated] = useState(false);

  const handleRuleCreation = () => {
    setRuleCreated(true);
  };

  return (
    <div className="App container mt-5">
      <h1 className="text-center">Rule Engine Application</h1>

      <div className="card mt-4">
        <div className="card-body">
          <h5 className="card-title">Create a Rule</h5>
          <RuleForm onRuleCreated={handleRuleCreation} />
        </div>
      </div>

      {ruleCreated && (
        <div className="card mt-4">
          <div className="card-body">
            <h5 className="card-title">Evaluate the Rule</h5>
            <RuleEvaluator />
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
